mapper.update:
UPDATE user
SET delete_flg = 1
WHERE user_id = #{userId}

mapper.select:
SELECT user_id, name, email, avatar, description, status, created_at
FROM user
WHERE user_id = #{userId}

SELECT user_id, name, email, avatar, description, status, created_at
FROM user
WHERE user_id = #{email}

SELECT COUNT(*)
FROM user
WHERE email = #{email}
